using NUnit.Framework;

namespace decodemorse;


[TestFixture]
class MorseRealTest
{
    [Test]
    public void TestSomething()
    {
        Assert.AreEqual("HEY JUDE", MorseCodeDecoder.DecodeMorse(MorseCodeDecoder.DecodeBitsAdvanced("0000000011011010011100000110000001111110100111110011111100000000000111011111111011111011111000000101100011111100000111110011101100000100000")));
    }

    [Test]
    public void TestDogString()
    {
        Assert.AreEqual("SOS! THE QUICK BROWN FOX JUMPS OVER THE LAZY DOG.", MorseCodeDecoder.DecodeMorse(MorseCodeDecoder.DecodeBitsAdvanced("")));
    }

    [Test]
    public void FinalTest()
    {
        Assert.AreEqual("MGY CQD CQD SOS TITANIC POSITION 41.44 N 50.24 W. REQUIRE IMMEDIATE ASSISTANCE. COME AT ONCE. WE STRUCK AN ICEBERG. SINKING", MorseCodeDecoder.DecodeMorse(MorseCodeDecoder.DecodeBitsAdvanced("")));
    }
}
