using FluentAssertions;
using NUnit.Framework;
using System.Numerics;

namespace dpheightandegg;


[TestFixture]
public class FabergeTest
{
    public void ShouldReturnRightHeight()
    {
        Faberge.Height(0, 14).Should().Be(0);
        Faberge.Height(2, 0).Should().Be(0);
        Faberge.Height(2, 14).Should().Be(105);
        Faberge.Height(7, 20).Should().Be(137979);
    }

    public void ShouldReturnRightBigIntHeight()
    {
        Faberge.OptimizedHeight(7, 500).Should().Be(BigInteger.Parse("1507386560013475"));
    }

    public void ShouldReturnRightUltraLargeHeight()
    {
        Faberge.Height(237, 500).Should().Be(
            BigInteger.Parse("431322842186730691997112653891062105065260343258332219390917925258990318721206767477889789852729810256244129132212314387344900067338552484172804802659")
        );
    }

    public void ShouldReturnRightSuperLargeHeight()
    {
        Faberge.FastHeight(9477, 10000).Should().Be(
            BigInteger.Parse("")
        );
    }

    [Test]
    public void ShouldReturnRightFastHeight()
    {
        //new BigInteger(137979), Faberge.Height(7,20)
        Faberge.Height(7, 20).Should().Be(137979);
        Faberge.Height(3000, BigInteger.Pow(2, 200)).Should().Be(new BigInteger(141903106));
    }
}

